import { useState, useEffect } from "react";
import axios from "axios";
import { FaInfoCircle } from 'react-icons/fa'; // Ícone fofo de informação
import "./App.css";

function App() {
  const [livros, setLivros] = useState([]);
  const [livroAtual, setLivroAtual] = useState({ titulo: "", autor: "", ano_publicacao: "", quantidade: "", preco: "", categoria: "" });
  const [modoEdicao, setModoEdicao] = useState(false);
  const [mostrarSobre, setMostrarSobre] = useState(false);

  useEffect(() => {
    buscarLivros();
  }, []);

  const buscarLivros = async () => {
    try {
      const resposta = await axios.get("http://localhost:3001/api/livros");
      setLivros(resposta.data);
    } catch (error) {
      console.error("Erro ao buscar livros:", error);
    }
  };

  return (
    <div className="App">
      <h1>Gerenciador de Livros</h1>

      {/* Botão Sobre com ícone */}
      <button className="sobre-btn" onClick={() => setMostrarSobre(true)}>
        <FaInfoCircle style={{ marginRight: "8px" }} />
        Sobre
      </button>

      {/* Modalzinho animado */}
      {mostrarSobre && (
        <div className="sobre-modal" onClick={() => setMostrarSobre(false)}>
          <div className="sobre-conteudo" onClick={(e) => e.stopPropagation()}>
            <h2>Sobre o Projeto 📚✨</h2>
            <p>Este projeto foi criado para gerenciar livros de uma livraria, permitindo cadastrar, editar e remover livros!</p>
            <p>Feito com muito amor usando React, Node.js, Express e MySQL! 💖</p>
            <button onClick={() => setMostrarSobre(false)}>Fechar</button>
          </div>
        </div>
      )}

      {/* Inputs e lista */}
      <div className="formulario">
        {/* inputs aqui */}
      </div>

      <div className="lista">
        {/* lista de livros */}
      </div>

      <footer>
        <p>Desenvolvido com 💖 por [Seu Nome]</p>
      </footer>
    </div>
  );
}

export default App;
