import { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [livros, setLivros] = useState([]);
  const [livroAtual, setLivroAtual] = useState({ titulo: "", autor: "", ano_publicacao: "", quantidade: "", preco: "", categoria: "" });
  const [modoEdicao, setModoEdicao] = useState(false);
  const [mostrarSobre, setMostrarSobre] = useState(false); // NOVO estado para abrir/fechar o "Sobre"

  useEffect(() => {
    buscarLivros();
  }, []);

  const buscarLivros = async () => {
    try {
      const resposta = await axios.get("http://localhost:3001/api/livros");
      setLivros(resposta.data);
    } catch (error) {
      console.error("Erro ao buscar livros:", error);
    }
  };

const adicionarLivro = async () => {
    if (!livroAtual.titulo || !livroAtual.autor || !livroAtual.ano) {
      alert("Preencha todos os campos!");
      return;
    }
    try {
      await axios.post("http://localhost:3001/api/livros", livroAtual);
      buscarLivros();
      setLivroAtual({ titulo: "", autor: "", ano: "" });
    } catch (error) {
      console.error("Erro ao adicionar livro:", error);
      alert("Erro ao adicionar livro.");
    }
  };

  const atualizarLivro = async () => {
    if (!livroAtual.titulo || !livroAtual.autor || !livroAtual.ano) {
      alert("Preencha todos os campos!");
      return;
    }
    try {
      await axios.put(`http://localhost:3001/api/livros/${livroAtual.id}`, livroAtual);
      buscarLivros();
      setLivroAtual({ titulo: "", autor: "", ano: "" });
      setModoEdicao(false);
    } catch (error) {
      console.error("Erro ao atualizar livro:", error);
      alert("Erro ao atualizar livro.");
    }
  };

  const deletarLivro = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/livros/${id}`);
      buscarLivros();
    } catch (error) {
      console.error("Erro ao deletar livro:", error);
      alert("Erro ao deletar livro.");
    }
  };

  const selecionarLivro = (livro) => {
    setLivroAtual(livro);
    setModoEdicao(true);
  };

  return (
    <div className="App">
      <h1>Gerenciador de Livros</h1>

      {/* BotÃ£o Sobre */}
      <button className="sobre-btn" onClick={() => setMostrarSobre(true)}>Sobre</button>

      {/* Modalzinho Sobre */}
      {mostrarSobre && (
        <div className="sobre-modal">
          <div className="sobre-conteudo">
            <h2>Sobre o Projeto</h2>
            <p>Este projeto foi desenvolvido para gerenciar livros de uma livraria, permitindo adicionar, editar e deletar informaÃ§Ãµes de estoque. ðŸ“šâœ¨</p>
            <p>Feito com muito carinho usando React, Node.js, Express e MySQL! ðŸ’»ðŸ’•</p>
            <button onClick={() => setMostrarSobre(false)}>Fechar</button>
          </div>
        </div>
      )}

      {/* Seu formulÃ¡rio e lista de livros continuam */}
      <div className="formulario">
        {/* Inputs para tÃ­tulo, autor, ano_publicacao, etc */}
      </div>

      <div className="lista">
        {/* Lista de livros */}
      </div>

      <footer>
        <p>Desenvolvido com ðŸ’– por Rafaella</p>
      </footer>
    </div>
  );
}

export default App;
