const express = require('express');
const cors = require('cors');
const bodyParser = require('body-parser');
const livroRoutes = require('./routes/livroRoutes');

const app = express();

app.use(cors());

app.use(bodyParser.json());

app.use('/api', livroRoutes);

module.exports = app;
